interface ISwapRouter {
    struct ExactInputSingleParams {
        address tokenIn;
        address tokenOut;
        uint24 fee;
        address recipient;
        // uint256 deadline; //remove this line for sepolia
        uint256 amountIn;
        uint256 amountOutMinimum;
