pragma solidity ^0.8.23;

interface AggregatorV3Interface {
    function latestRoundData()
        external
        view
        returns (uint80 roundId, int256 answer, uint256 startedAt, uint256 updatedAt, uint80 answeredInRound);

    function decimals() external view returns (uint8);
}

contract MockUSDTAggregatorV3 is AggregatorV3Interface {
    // Constants for mock USD/USDT price data
    uint80 private constant ROUND_ID = 12345;
    int256 private constant PRICE = 100000000; // 1.00000000 USD per USDT (8 decimals)
    uint256 private constant STARTED_AT = 1704067200; // Jan 1, 2024 timestamp
    uint256 private constant UPDATED_AT = 1704067200; // Jan 1, 2024 timestamp
    uint80 private constant ANSWERED_IN_ROUND = 12345;
    uint8 private constant DECIMALS = 8;

    /**
     * @dev Returns the latest round data with constant values
     * This simulates a USD/USDT price feed (approximately 1:1 parity)
     */
    function latestRoundData()
        external
        view
        override
        returns (
            uint80 roundId,
            int256 answer,
            uint256 startedAt,
            uint256 updatedAt,
            uint80 answeredInRound
        )
    {
        return (
            ROUND_ID,
            PRICE,
            STARTED_AT,
            UPDATED_AT,
            ANSWERED_IN_ROUND
        );
    }

    /**
     * @dev Returns the number of decimals used by the price feed
     */
    function decimals() external view override returns (uint8) {
        return DECIMALS;
    }

    // Optional: Add a function to get the human-readable price
    function getFormattedPrice() external view returns (string memory) {
        return "1.00000000";
    }
}
