        "SourceCode": "// SPDX-License-Identifier: MIT\r\npragma solidity ^0.8.23;\r\n\r\ninterface AggregatorV3Interface {\r\n    function latestRoundData()\r\n        external\r\n        view\r\n        returns (uint80 roundId, int256 answer, uint256 startedAt, uint256 updatedAt, uint80 answeredInRound);\r\n\r\n    function decimals() external view returns (uint8);\r\n}\r\n\r\ncontract MockUSDTAggregatorV3 is AggregatorV3Interface {\r\n    // Constants for mock USD/USDT price data\r\n    uint80 private constant ROUND_ID = 12345;\r\n    int256 private constant PRICE = 100000000; // 1.00000000 USD per USDT (8 decimals)\r\n    uint256 private constant STARTED_AT = 1704067200; // Jan 1, 2024 timestamp\r\n    uint256 private constant UPDATED_AT = 1704067200; // Jan 1, 2024 timestamp\r\n    uint80 private constant ANSWERED_IN_ROUND = 12345;\r\n    uint8 private constant DECIMALS = 8;\r\n\r\n    /**\r\n     * @dev Returns the latest round data with constant values\r\n     * This simulates a USD/USDT price feed (approximately 1:1 parity)\r\n     */\r\n    function latestRoundData()\r\n        external\r\n        view\r\n        override\r\n        returns (\r\n            uint80 roundId,\r\n            int256 answer,\r\n            uint256 startedAt,\r\n            uint256 updatedAt,\r\n            uint80 answeredInRound\r\n        )\r\n    {\r\n        return (\r\n            ROUND_ID,\r\n            PRICE,\r\n            STARTED_AT,\r\n            UPDATED_AT,\r\n            ANSWERED_IN_ROUND\r\n        );\r\n    }\r\n\r\n    /**\r\n     * @dev Returns the number of decimals used by the price feed\r\n     */\r\n    function decimals() external view override returns (uint8) {\r\n        return DECIMALS;\r\n    }\r\n\r\n    // Optional: Add a function to get the human-readable price\r\n    function getFormattedPrice() external view returns (string memory) {\r\n        return \"1.00000000\";\r\n    }\r\n}",
